	public static int[][] DAYS_OF_MONTH = {
    	{29}, {30}, {29, 29}, {29, 30}, {30, 29}, {30, 30}
    };
    
	public static final int LUNA_BEGIN_YEAR = 1799;
	public static final int LUNA_END_YEAR = 1799 - 1 + LUNA_MONTH_TABLE.length;

