package basic.external.ulid;

import org.junit.jupiter.api.Test;

import com.github.f4b6a3.ulid.Ulid;
import com.github.f4b6a3.ulid.UlidCreator;

/** ULID 테스트 케이스 */
public class UlidTest {
    
    //  더 많은 정보는 아래 사이트 
    // https://github.com/f4b6a3/ulid-creator
    @Test
    public void testUlid(){ 
        // The first 48 bits represent the number of milliseconds since Unix Epoch,
        // The remaining 80 bits are generated by a secure random number generato
        Ulid ulid = UlidCreator.getUlid();
        System.out.println(ulid.toString());
    }
    @Test
    public void testUlid2(){ 
        // The first 48 bits represent the number of milliseconds since Unix Epoch,
        //  The random component is incremented by 1 whenever the current millisecond is equal to the previous one. Its main advantage is speed.
        Ulid ulid = UlidCreator.getMonotonicUlid();
        System.out.println(ulid.toString());
    }

}
